    public function item($id)
    {
        if (!UsersController::hasRule('packages', 'read')) {
            return redirect('/admin');
        }

        // try {
            $item = Item::query()->find($id);

            if (!$item) {
                return redirect('/admin/items');
            }

            if ($item->deleted == 1) {
                return redirect('/admin/items');
            }

            if (is_null($item->publishAt)) {
                $item->publishAt = '';
            } else {
                $item->publishAt = Carbon::createFromFormat('Y-m-d H:i:s', $item->publishAt)->format('Y-m-d H:i');
            }

            if (is_null($item->showUntil)) {
                $item->showUntil = '';
            } else {
                $item->showUntil = Carbon::createFromFormat('Y-m-d H:i:s', $item->showUntil)->format('Y-m-d H:i');
            }

            if ($item->expireAfter > 0 && $item->expireUnit > 0)
                $item->expireAfter = ExpireHelper::GetOriginPeriodValue($item->expireUnit, $item->expireAfter);

            $commands = Command::where('item_type', Command::ITEM_COMMAND)
                ->where('item_id', $item->id)
                ->get();

            if ($commands->isEmpty()) {
                $item->cmds = [];
            } else {
                foreach ($commands as $command) {
                    $command['servers'] = array_unique(ItemServer::where('item_servers.type', ItemServer::TYPE_CMD_SERVER)
                        ->join('commands', 'commands.id', '=', 'item_servers.item_id')
                        ->where('commands.item_id', $item->id)->select('item_servers.server_id')
                        ->get()
                        ->pluck('server_id')
                        ->toArray(),SORT_NUMERIC);
                    $command['delay_value'] = CommandHelper::GetOriginDelayValue($command['delay_unit'], $command['delay_value']);
                    $command['repeat_value'] = CommandHelper::GetOriginDelayValue($command['repeat_unit'], $command['repeat_value']);
                }
                $item->cmds = $commands->toArray();
            }

            $categories = Category::query()->where('url', '<>', '')->where('deleted', 0)->orderBy('sorting')->get();
            $servers = Server::query()->where('deleted', 0)->get();
            $vars = Variable::query()->where('deleted', 0)->get();
            $itemVars = ItemVar::where('item_id', $item->id)->select('var_id')->get()->pluck('var_id')->all();
            $discordRoles = $this->settings->discord_bot_enabled
                ? DiscordRole::query()->where('deleted', 0)->get()
                : collect();

            $itemRoles = ItemRole::where('item_id', $item->id)->select('role_id')->get()->pluck('role_id')->all();

            $reqs = Item::query()
                ->select('items.id', 'items.name', 'categories.name as category_name')
                ->where('items.id', '<>', $id)
                ->where('items.deleted', 0)
                ->join('categories', 'categories.id', '=', 'items.category_id')
                ->get();
            $ecoServers = ItemServer::where('item_id', $id)
                ->where('type', ItemServer::TYPE_ECONOMY_SERVER)
                ->get()
                ->pluck('server_id')
                ->toArray();

            if (empty($item->chargeback_commands) || $item->chargeback_commands == 'null') {
                $item->chargeback_commands = '[]';
            }
            $isItemExist = true;

            // If item attached to comparison category - get comparison data
            $category = Category::where('id', $item->category_id)->first();
            $comparisonItemValues = [];
            if ($category->is_comparison == 1)
                $comparisonItemValues = ItemComparison::where('item_id', $item->id)->get()->pluck('value', 'comparison_id')->toArray();

            return view('admin.items.item', compact('isItemExist', 'item', 'categories', 'vars', 'itemVars', 'discordRoles', 'itemRoles', 'reqs', 'servers', 'comparisonItemValues', 'ecoServers'));
    }

    /**
     * Reorder items and categories
     *
     * @param Request $request
     * @return JsonResponse
     */
    public function updateSort(ReorderItemsRequest $request): JsonResponse
    {
        if (!UsersController::hasRule('packages', 'write')) {
            return response()->json(['message' => 'Not authorized'], Response::HTTP_UNAUTHORIZED);
        }

        $old_index = intval($request->old_index);
        $new_index = intval($request->new_index);
        $object_id = intval($request->object_id);

        // If items sorting
        if ($request->type == 'item') {
            $item = Item::where('id', $object_id)
                ->where('deleted', 0)
                ->first();

            if (!$item) {
                return response()->json(['message' => 'Item not found'], 404);
            }

            // If reorder in the same category
            if ($request->from_category_id == $request->to_category_id) {
                $items = Item::where('category_id', $request->from_category_id)
                    ->where('deleted', 0)
                    ->orderBy('sorting', 'DESC')
                    ->get();

                // Reorder items
                SortHelper::updateItemsSorting($items, $item, $old_index, $new_index);

                return response()->json(['message' => 'Success']);
            }

            // If reorder in different categories
            $itemsFromCategory = Item::where('category_id', $request->from_category_id ?? 0)
                ->where('deleted', 0)
                ->orderBy('sorting', 'DESC')
                ->get();
            $itemsToCategory = Item::where('category_id', $request->to_category_id ?? 0)
                ->where('deleted', 0)
                ->orderBy('sorting', 'DESC')
                ->get();

            // Change category_id of the item
            $oldCategoryId = $item->category_id;
            $item->category_id = $request->to_category_id ?? 0;
            $item->save();

            $itemsFromCategory->forget($old_index);

            SortHelper::adjustSortingAfterMoving($itemsFromCategory, $old_index);
            SortHelper::insertItemAtNewIndex($itemsToCategory, $item, $new_index);

            return response()->json(['message' => 'Success']);
        }

        // If categories sorting
        $categoryFrom = $request->from_category_id ?? 0;
        $categoryTo = $request->to_category_id ?? 0;
        $category = Category::where('id', $object_id)
            ->where('deleted', 0)
            ->first();

        if (!$category) {
            return response()->json(['message' => 'Category not found'], 404);
        }

        if ($categoryFrom == $categoryTo) {
            $categories = Category::where('parent_id', $categoryFrom)
                ->where('deleted', 0)
                ->orderBy('sorting', 'ASC')
                ->get();

            // Reorder categories
            SortHelper::updateCategoriesSorting($categories, $category, $old_index, $new_index);

            return response()->json(['message' => 'Success']);
        }

        // If reorder in different categories
        $categoriesFrom = Category::where('parent_id', $categoryFrom)
            ->where('deleted', 0)
            ->orderBy('sorting', 'DESC')
            ->get();

        $categoriesTo = Category::where('parent_id', $categoryTo)
            ->where('deleted', 0)
            ->orderBy('sorting', 'DESC')
            ->get();

        $categoryToURL = '';
        if ($categoryTo > 0) {
            $categoryToData = Category::where('id', $categoryTo)
                ->where('deleted', 0)
                ->select('url')
                ->first();

            if (!is_null($categoryToData)) {
                $categoryToURL = $categoryToData->url;
            } else {
                return response()->json(['message' => 'Destination category not found'], 404);
            }
        }

        // Reordering categories
        $oldParentId = $category->parent_id;
        $oldURL = $category->url;
        $category->parent_id = $categoryTo ?? 0;

        $partsURL = explode('/', $category->url);
        if ($categoryToURL !== '') {
            $newURL = $categoryToURL . '/' . end($partsURL);
        } else {
            $lastPart = end($partsURL);
            $newURL = $lastPart; // For root category
        }
        $category->url = $newURL;

        $category->save();

        // Updating the URLs of all subcategories recursively
        function updateSubcategoryURLs($category, $newParentURL): void
        {
            $subcategories = Category::where('parent_id', $category->id)
                ->where('deleted', 0)
                ->orderBy('sorting', 'DESC')
                ->get();

            foreach ($subcategories as $subcategory) {
                $oldURL = $subcategory->url;
                $partsURL = explode('/', $subcategory->url);
                $subcategory->url = $newParentURL . '/' . end($partsURL);
                $subcategory->save();

                updateSubcategoryURLs($subcategory, $subcategory->url);
            }
        }

        updateSubcategoryURLs($category, $newURL);

        $categoriesFrom->forget($old_index);

        SortHelper::adjustSortingAfterMoving($categoriesFrom, $old_index);

        SortHelper::insertItemAtNewIndex($categoriesTo, $category, $new_index);

        return response()->json(['message' => 'Success']);
    }

    public function getComparison($id, $c_url)
    {
        if (!UsersController::hasRule('packages', 'read')) {
            return redirect('/admin');
        }

        $comparison = [];
        $category = Category::where('url', $c_url)->where('deleted', 0)->first();

        if ($category->is_comparison == 1) {
            $comparison = json_decode($category->comparison, true);
        }

        return $comparison;
    }
}
