    public function save($id, Request $r)
    {
        if (!UsersController::hasRule('packages', 'write')) {
            return redirect('/admin');
        }

        $commands = [];
        $is_server_choice = 0;

        $r->validate([
            'name' => 'required|string|max:255',
            'price' => ['required', 'regex:/^[\d\.,]+$/'],
        ]);

        if ($r->input('type') == Item::MINECRAFT_PACKAGE || $r->input('type') == Item::MINECRAFT_AND_GIFTCARD) {
            if ($r->has('minecraft')) {
                $globalServers = [];

                foreach ($r->input('minecraft') as $command) {
                    if (isset($command['servers']))
                        foreach ($command['servers'] as $subCommand)
                            $globalServers[] = $subCommand;
                }
                $globalServers = array_unique($globalServers);

                foreach ($r->input('minecraft') as $command) {
                    $is_server_choice = $is_server_choice === 1 || (isset($command['is_server_choice']) && $command['is_server_choice'] == 'on') ? 1 : 0;
                    if (isset($command['commands'])) {
                        foreach ($command['commands'] as $subCommand)
                        {
                            if (!isset($subCommand['servers']) || count($subCommand['servers']) == 0)
                                $subCommand['servers'] = $globalServers;

                            $commands[] = $subCommand;
                        }
                    }
                }
            }
        }

