    public function delete($id)
    {
        if (!UsersController::hasRule('packages', 'del')) {
            return redirect('/admin');
        }

        $expireItems = DB::table('payments')
          ->join('carts', 'carts.id', '=', 'payments.cart_id')
          ->join('users', 'users.id', '=', 'carts.user_id')
          ->join('cart_items', 'cart_items.cart_id', '=', 'carts.id')
          ->join('items', 'items.id', '=', 'cart_items.item_id')
          ->select('items.id', 'items.name', 'items.price', 'users.username', 'users.ip_address', 'users.uuid', DB::raw('cart_items.id as cart_items_id'), 'cart_items.cart_id', 'items.is_server_choice', 'cart_items.count', DB::raw('payments.id as payment_id'))
          ->where([
              ['items.id', '=', $id],
              ['payments.status', '=', '1'],
          ])->get();

        foreach ($expireItems as $expireItem) {
            $cart = (object) ['count' => $expireItem->count, 'id' => $expireItem->cart_items_id, 'cart_id' => $expireItem->cart_id];

            $servers = [];

            if ($expireItem->is_server_choice == 1){
                $cartSelectServers = CartSelectServer::where([['cart_id', $expireItem->cart_id], ['item_id', $expireItem->id]])->get();
                if (!$cartSelectServers->isEmpty())
                    $servers = $cartSelectServers->servers()->where('deleted', 0)->get();
            }

            $servers = collect($servers);
            if ($servers->isEmpty())
            {
                $servers = ItemServer::where([['type', ItemServer::TYPE_CMD_SERVER], ['item_id', $expireItem->id]])->get();
                if ($servers->isEmpty())
                {
                    $servers = Server::where('deleted', 0)->get();
                } else {
                    $servers = Server::whereIn('id', $servers->pluck('server_id')->toArray())->get();
                }
            }

            if ($this->settings->withdraw_game == 'minecraft') {
                $user = (object)['username' => $expireItem->username, 'ip_address' => $expireItem->ip_address, 'uuid' => $expireItem->uuid];
                if (ItemController::giveItem(Command::EVENT_REMOVED, $servers, $expireItem, $user, $cart, $expireItem->id)){
                    Payment::where('id', $expireItem->payment_id)->update(['status' => 3]);
                }
            }
        }

        $commandsToDelete = Command::where('item_id', $id)->select('id')->get()->pluck('id')->toArray();
        if (!empty($commandsToDelete) && count($commandsToDelete) > 0){
            $deletePlaceholder = implode(",", array_fill(0, count($commandsToDelete), '?'));
            DB::delete('DELETE item_servers FROM item_servers JOIN commands ON commands.id = item_servers.item_id WHERE commands.item_id IN ('.$deletePlaceholder.')', $commandsToDelete);
        }
        Command::where('item_type', Command::ITEM_COMMAND)->where('item_id', $id)->delete();

        ItemVar::query()->where('item_id', $id)->delete();
        ItemRole::query()->where('item_id', $id)->delete();
        RequiredItem::query()->where('item_id', $id)->delete();
        ItemComparison::query()->where('item_id', $id)->delete();
        $item = Item::query()->find($id);
        $item->update(
            [
                'name' => '[DELETED] ' . Item::query()->find($id)->name,
                'deleted' => 1,
            ]
        );

        SecurityLog::create([
            'admin_id' => \Auth::guard('admins')->user()->id,
            'method' => SecurityLog::DELETE_METHOD,
            'action' => SecurityLog::ACTION['packages'],
            'action_id' => $id,
        ]);

        return redirect('/admin/items');
    }

